mapper.update:
update sys_role
role_name = #{roleName}, role_code = #{roleCode}, description = #{description}, deleteable_flag = #{deleteableFlag}, role_type = #{roleType}, create_user_uid = #{createUserUid}, create_time = #{createTime}, edit_user_uid = #{editUserUid}, edit_time = #{editTime}
where role_uid = #{roleUid}

mapper.delete:
delete from sys_role 
where role_uid in

    #{item}

mapper.select:
select

from sys_role


        and role_uid = #{record.roleUid}


        and role_name = #{record.roleName}


        and role_code = #{record.roleCode}


        and description = #{record.description}


        and deleteable_flag = #{record.deleteableFlag}


        and role_type = #{record.roleType}


        and create_user_uid = #{record.createUserUid}


        and create_time = #{record.createTime}


        and edit_user_uid = #{record.editUserUid}


        and edit_time = #{record.editTime}

select
  count(role_uid)
from sys_role


        and role_uid = #{record.roleUid}


        and role_name = #{record.roleName}


        and role_code = #{record.roleCode}


        and description = #{record.description}


        and deleteable_flag = #{record.deleteableFlag}


        and role_type = #{record.roleType}


        and create_user_uid = #{record.createUserUid}


        and create_time = #{record.createTime}


        and edit_user_uid = #{record.editUserUid}


        and edit_time = #{record.editTime}
